import 'package:e_commerce_project/core/constant/app_routes_name.dart';import 'package:e_commerce_project/routes/route_app.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';abstract class CheckEmailController extends GetxController {  check_email();  go_to_verify_code();}class CheckEmailControllerImp extends CheckEmailController {  TextEditingController text_field_email_controller = TextEditingController();  GlobalKey<FormState> formState = GlobalKey<FormState>();  @override  void dispose() {    text_field_email_controller.dispose();    super.dispose();  }  @override  go_to_verify_code() {    Get.offNamed(AppRouteName.verify_code_sign_up);    // throw UnimplementedError();  }  @override  check_email() {    if (formState.currentState!.validate())    {      print('check');    }  }}