import 'package:awesome_dialog/awesome_dialog.dart';import 'package:e_commerce_project/core/class/crud.dart';import 'package:e_commerce_project/core/constant/AppLinkData.dart';import 'package:e_commerce_project/core/constant/app_routes_name.dart';import 'package:e_commerce_project/core/functions/statusRequest.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';abstract class VerifyCodeController extends GetxController {  checkCode(String x, String y, BuildContext context);  go_to_reset_password(String email);}class VerifyCodeControllerImp extends VerifyCodeController {  StatusRequest statusRequest = StatusRequest.init;  @override  checkCode(String email, String verify_code, context) async {    print('%%%%%%%%%%%%%%%%%%%%%%%%');    print('%%%%%%%%%%%%%%%%%%%%%%%%');    print('%%%%%%%%%%%%%%%%%%%%%%%%');    print(email);    print(verify_code);    print('%%%%%%%%%%%%%%%%%%%%%%%%');    print('%%%%%%%%%%%%%%%%%%%%%%%%');    print('%%%%%%%%%%%%%%%%%%%%%%%%');    try {      statusRequest = StatusRequest.loading;      update();      await Crud().postData('${AppLinkData.link_check_code_sign_up}', {        'email': email,        'verify_code': verify_code,      }).then((value) {        value.fold((l) {          statusRequest = l;          update();        }, (r) {      print(r);          if (r['status_verify'] == 'success') {            go_to_reset_password(email);          } else {            statusRequest = StatusRequest.failure;            update();            AwesomeDialog(context: context, body: Text('Error')).show();            statusRequest = StatusRequest.init;            update();          }        });      });    } catch (e) {      print(e);      statusRequest = StatusRequest.failure;      update();      AwesomeDialog(context: context, body: Text('Error')).show();      statusRequest = StatusRequest.init;      update();    }  }  @override  go_to_reset_password(String email) {    Get.offNamed(AppRouteName.reset_password, arguments: {      'email': email,    });  }}