import 'package:e_commerce_project/core/class/crud.dart';import 'package:e_commerce_project/core/constant/AppLinkData.dart';import 'package:e_commerce_project/data/data_source/static/static.dart';class Address {  Crud crud;  Address(this.crud);  add_address(    dynamic user_id,    dynamic address_name,    dynamic city,    dynamic street,      dynamic lat,      dynamic long,  ) async {    try {      return await crud.postData(AppLinkData.add_address, {        'user_id': user_id,        'address_name': address_name,        'city': city,        'street': street,        'lat': lat,        'long': long,      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      print(e);      return e.toString();    }  }  edit_address(int address_id, String address_name, String city, String street,      double lat, double long) async {    return await crud.postData(AppLinkData.edit_address, {      'user_id': '$address_id',      'address_name': '$address_name',      'city': '$city',      'street': '$street',      'lat': '$lat',      'long': '$long',    }).then((value) {      return value.fold((l) => l, (r) {        return r;      });    });  }  delete_address(int address_id) async {    return await crud.postData(AppLinkData.delete_address, {      'address_id': '$address_id',    }).then((value) {      print(value);      return value.fold((l) => l, (r) {        return r;      });    });  }  view_address(String _userId) async {    try {      return await crud          .postData(AppLinkData.view_address, {'user_id': _userId}).then((value) {            print('888888888888888888888888888');            print(value);        return value.fold((l) => l, (r) {          print('888888888888888888888888888');          print('88888d8888888888888888888888');          print('888888f888888888888888888888');print(r);          return r;        });      });    } catch (e) {      print('Error ;