import 'package:e_commerce_project/core/class/crud.dart';import 'package:e_commerce_project/core/constant/AppLinkData.dart';class ViewFavorite {  Crud crud;  ViewFavorite(this.crud);  getData(int user_id) async {    return await crud.postData(AppLinkData.view_favorite, {      'user_id': '$user_id',    }).then((value) {      return value.fold((l) => l, (r) {        // print('%%%%%%%%%%%%%%%%%%%%%%%%%f%%%%%%%');        // print('%%%%%%%%%%%%%%%%%%%%%%%%s%%%%%%%%');        //        // print(r);        return r;      });    });  }}