import 'package:e_commerce_project/core/class/crud.dart';import 'package:e_commerce_project/core/constant/AppLinkData.dart';class UpdateFavorite {  Crud crud;  UpdateFavorite(this.crud);  getData(int is_favorite, int user_id, int item_id) async {    return await crud.postData(AppLinkData.update_favorite, {      'is_favorite': '$is_favorite',      'fav_user_id': '$user_id',      'fav_item_id': '$item_id',    }).then((value) {      print(value);      return value.fold((l) => l, (r) {        // print('%%%%%%%%%%%%%%%%%%%%%%%%%f%%%%%%%');        // print('%%%%%%%%%%%%%%%%%%%%%%%%s%%%%%%%%');        //        // print(r);        return r;      });    });  }}