import 'package:flutter/material.dart';void main() {  runApp(Masbaha());}class Masbaha extends StatefulWidget {  const Masbaha({Key? key}) : super(key: key);  @override  State<Masbaha> createState() => _MasbahaState();}class _MasbahaState extends State<Masbaha> {  int count = 0;  @override  Widget build(BuildContext context) {    return MaterialApp(      debugShowCheckedModeBanner: false,      home: Scaffold(        appBar: AppBar(          elevation: 0,          backgroundColor: Colors.black,          centerTitle: true,          title: Text(            'مسبحة',            style: TextStyle(              fontSize: 35,            ),          ),        ),        body: Container(            child: Column(          // mainAxisAlignment: MainAxisAlignment.spaceAround,          children: [            SizedBox(              height: 200,            ),            Row(              mainAxisAlignment: MainAxisAlignment.spaceEvenly,              children: [                Container(                  color: Colors.blue,                  width: 100,                  height: 30,                  child: Center(                    child: Text('$count'),                  ),                ),                  SizedBox(                     width: 20,                  ) ,                InkWell(                  onTap: (){                    setState(() {                      count = 0 ;                    });                  },                  child: Container(                    color: Colors.blue,                    width: 100,                    height: 30,                    child: Center(                      child: Text('صفر'),                    ),                  ),                ),              ],            ),            // Spacer(),            SizedBox(              height: 50,            ),            Expanded(              child: InkWell(                onTap: () {                  setState(() {                    count++;                  });                },                child: Container(                  width: double.infinity,                  decoration: BoxDecoration(                    color: Colors.blue,                    borderRadius: BorderRadius.circular(500),                  ),               child: Center(                 child: Icon(Icons.add,size: 150,),               ),                ),              ),            ),          ],        )),      ),    );  }}