import 'package:e_commerce_project/controller/auth/forget_password_controller.dart';import 'package:e_commerce_project/controller/auth/resetpassword_controller.dart';import 'package:e_commerce_project/view/widgets/auth/app_text_field.dart';import 'package:e_commerce_project/view/widgets/auth/custom_button_auth.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:e_commerce_project/core/functions/valide.dart';class ResetPasswordScreen extends StatelessWidget {  const ResetPasswordScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    ResetPasswordControllerImp controllerImp =        Get.put(ResetPasswordControllerImp());    return Scaffold(      // backgroundColor: Color(0xFFF5F7FBFF),      appBar: AppBar(        backgroundColor: Color(0xFFF5F7FBFF),        elevation: 0,        actions: [          IconButton(            onPressed: () {},            icon: Icon(              Icons.language,            ),          ),        ],        centerTitle: true,        title: Text(          "Reset Password",          style: Theme.of(context).textTheme.headline1!.copyWith(              color: Colors.black, fontSize: 24, fontWeight: FontWeight.bold),        ),      ),      body: Container(        padding: EdgeInsets.all(15),        child: Center(          child: Container(            height: Get.height / 2,            child: Form(                key: controllerImp.formState,                child: ListView(                  children: [                    space(30, 0),                    Align(                      alignment: AlignmentDirectional.center,                      child: Text(                        "New Password",                        style: Theme.of(context).textTheme.headline1!.copyWith(                            color: Colors.black,                            fontSize: 24,                            fontWeight: FontWeight.bold),                      ),                    ),                    space(30, 0),                    AppTextField(                      label: 'password',                      hintText: 'Enter password',                      icon: Icons.lock_outline,                      controller: controllerImp.password,                      context: context,                      valid: (val) {                        return validInput(val!, 6, 100, 'password');                      },                    ),                    space(15, 0),                    AppTextField(                      label: 'Again Password',                      hintText: 'Re Enter Email',                      icon: Icons.email_outlined,                      controller: controllerImp.re_password,                      context: context,                      valid: (val) {                        return validInput(val!, 6, 100, 'password');                      },                    ),                    space(20, 0),                    Custom_Button_Auth(                        txt: 'Save New Password',                        onPressed: () {                          if (controllerImp.formState.currentState!                              .validate()) {                            controllerImp.reset_password(context);                          }                        }),                  ],                )),          ),        ),      ),    );  }}// محمد صيام// يا امتي النوم طال