import 'package:e_commerce_project/controller/home/home_controller.dart';import 'package:e_commerce_project/controller/search_controller.dart';import 'package:e_commerce_project/core/constant/AppLinkData.dart';import 'package:e_commerce_project/core/constant/app_routes_name.dart';import 'package:e_commerce_project/data/data_source/static/static.dart';import 'package:e_commerce_project/data/model/item_model.dart';import 'package:e_commerce_project/view/widgets/home/home_screen_widgets.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';class Search_Screen extends StatelessWidget {  const Search_Screen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    Get.lazyPut(() => SearchController(),fenix: false);    return Scaffold(      // appBar: AppBar(),      body: GetBuilder<SearchController>(builder: (logic) {        return Container(          margin: EdgeInsets.all(5),          child: Column(            children: [              SizedBox(                height: 40,              ),              Custom_Field_Search(),              Expanded(                child: Container(                  margin: EdgeInsets.all(5),                  width: double.infinity,                  child: ListView.separated(                    itemCount: logic.itemSearching.length,                    itemBuilder: (context, index) {                      return CustomCardSearchView(                        logic.itemSearching[index],                      );                    },                    separatorBuilder: (context, index) {                      return Container(                        height: 2,                      );                    },                    padding: EdgeInsets.zero,                  ),                ),              ),            ],          ),        );      }),    );  }}Widget CustomCardSearchView(ItemModel item) {  return GetBuilder<SearchController>(builder: (logic) {    return Container(      margin: EdgeInsets.symmetric(vertical: 5, horizontal: 5),      width: double.infinity,      // height: 100,decoration: BoxDecoration(  color: Colors.black26,borderRadius: BorderRadius.circular(15),),      child: ListTile(        onTap: () {          logic.go_to_details(item);        },        leading: Image.network('${AppLinkData.image_items}/${item.itemImage}'),        minLeadingWidth: 50,        title: Text(          is_en == true ? '${item.itemName}' : '${item.itemNameAr}',          style: Get.textTheme.bodyText1!.copyWith(fontSize: 16),        ),        subtitle: Text(          is_en == true ? '${item.itemDesc}' : '${item.itemDescAr}',          style: Get.textTheme.bodyText1,          maxLines: 1,          overflow: TextOverflow.ellipsis,        ),        minVerticalPadding: 0,        // contentPadding: EdgeInsets.zero,        // trailing: Text('g97h0ijpo'),      ),    );  });}Widget Custom_Field_Search() {  return SizedBox(    height: 50,    child: Row(      children: [        Expanded(          child: Stack(            children: [              InkWell(                child: GetBuilder<SearchController>(builder: (logic) {                  return TextFormField(                    autofocus: true,                    onChanged: (val) {                      logic.search(val);                    },                    decoration: InputDecoration(                      focusColor: Get.theme.primaryColor,                      hoverColor: Colors.black26,                      prefixIcon: IconButton(                        onPressed: () {},                        color: Get.theme.iconTheme.color,                        // hoverColor: ,                        icon: Icon(Icons.search),                      ),                      hintText: 'search',                      hintStyle: Get.textTheme.bodyText1,                      border: OutlineInputBorder(                        borderRadius: BorderRadius.circular(8),                        borderSide: BorderSide.none,                      ),                      fillColor: Get.theme.focusColor,                      filled: true,                    ),                  );                }),                splashColor: Colors.white12.withOpacity(0.01),              ),            ],          ),        ),        SizedBox(          width: 10,        ),        Container(          decoration: BoxDecoration(            color: Get.theme.focusColor,            borderRadius: BorderRadius.circular(8),          ),          width: 60,          padding: EdgeInsets.symmetric(vertical: 7),          child: IconButton(            onPressed: () {},            icon: Icon(              color: Get.theme.iconTheme.color,              Icons.notifications_active_outlined,            ),          ),        ),      ],    ),  );}