import 'package:e_commerce_project/controller/localization/localization_controller.dart';import 'package:e_commerce_project/core/constant/app_routes_name.dart';import 'package:e_commerce_project/core/localization/translation.dart';import 'package:e_commerce_project/core/theme/theme.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';class SettingsScreen extends StatelessWidget {  const SettingsScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    ThemeController theme_controller = Get.put(ThemeController());    return Scaffold(      appBar: AppBar(        elevation: 0,        backgroundColor: Theme.of(context).appBarTheme.backgroundColor,        title: Text(          'settings'.tr,          style: Theme.of(context).textTheme.headline1!.copyWith(                fontWeight: FontWeight.w600,                fontSize: 22,              ),        ),        // centerTitle: true,      ),      body: Container(        child: Padding(          padding: const EdgeInsets.all(8.0),          child: ListView(            children: [              GetBuilder<ThemeController>(                builder: (logic) {                  return ListTile(                    title: Text(                      'theme_app'.tr,                      style: Get.textTheme.headline1!.copyWith(                        fontSize: 18,                      ),                    ),                    // subtitle: Text(""),                    trailing: Switch(                      onChanged: (val) {                        logic.updateTheme();                        logic.change_theme_val_switch(val);                      },                      value: logic.theme_is_dark!,                    ),                  );                },              ),              GetBuilder<LocaleController>(                builder: (logic) {                  return ListTile(                    title: Text(                      'language'.tr,                      style: Get.textTheme.headline1!.copyWith(                        fontSize: 18,                      ),                    ),                    // subtitle: Text(""),                    trailing: Switch(                      onChanged: (val) {                        logic.changeLang();                        print(val);                      },                      value: logic.language == Locale('en') ? true : false,                    ),                  );                },              ),              ListTile(                title: Text(                  'disable_notification'.tr,                  style: Get.textTheme.headline1!.copyWith(                    fontSize: 18,                  ),                ),                // subtitle: Text(""),                trailing: Switch(                  onChanged: (val) {                    print(val);                  },                  value: false,                ),              ),              ListTile(                onTap: ()                {                  Get.toNamed(AppRouteName.address) ;                },                title: Text(                  'address'.tr,                  style: Get.textTheme.headline1!.copyWith(                    fontSize: 18,                  ),                ),                trailing: Icon(Icons.location_pin),              ),              ListTile(                title: Text(                  'about_us'.tr,                  style: Get.textTheme.headline1!.copyWith(                    fontSize: 18,                  ),                ),                trailing: Icon(Icons.question_mark),              ),              ListTile(                title: Text(                  'contact_us'.tr,                  style: Get.textTheme.headline1!.copyWith(                    fontSize: 18,                  ),                ),                trailing: Icon(Icons.phone_callback_sharp),              ),              ListTile(                title: Text(                  'logout'.tr,                  style: Get.textTheme.headline1!.copyWith(                    fontSize: 18,                  ),                ),                trailing: Icon(Icons.logout_outlined),              ),            ],          ),        ),      ),    );  }}