import 'dart:math';import 'package:e_commerce_project/core/services/services.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';//change lang and save in shared pref//on init lang in all conditionsclass LocaleController extends GetxController {  MyServices services = Get.find();  Locale? language ;  changeLang(String codeLang) {    Locale locale = Locale(codeLang);    services.sharedPreferences.setString('lang', codeLang);    Get.updateLocale(locale);  }  @override  void onInit() {    String? codeLang = services.sharedPreferences.getString('lang');    if (codeLang == 'ar') {      language = Locale('ar');    } else if (codeLang == 'en') {      language = Locale('en');    } else {      language = Get.deviceLocale;    }    super.onInit();  }}