import 'package:flutter/material.dart';Widget AppTextField({  required String label,  required String hintText,  bool is_num = false,  bool obs = false,  bool is_avliabe = false,  void Function()? obs_fun,   IconData icon = Icons.add,  required TextEditingController controller,  required BuildContext context,  required String? Function(String?)? valid,}) {  return TextFormField(    controller: controller,    obscureText: obs,    validator: valid,    keyboardType: is_num == true ? TextInputType.number : TextInputType.text,    decoration: InputDecoration(      suffix: IconButton(        onPressed: obs_fun,        icon: Icon(          obs == true ? Icons.code_off_outlined : Icons.remove_red_eye,        ),      ),      hintText: hintText,      hintStyle: TextStyle(        fontSize: 13,      ),      floatingLabelBehavior: FloatingLabelBehavior.always,      contentPadding: EdgeInsets.symmetric(vertical: 5, horizontal: 30),      border: OutlineInputBorder(        borderRadius: BorderRadius.circular(20),      ),      label: Container(        margin: EdgeInsets.symmetric(          horizontal: 5,        ),        child: Text(label),      ),      labelStyle: TextStyle(        color: Colors.black54,        fontSize: 18      ),      suffixIcon: Icon(icon),    ),  );}Widget space(double height, double width) => SizedBox(      height: height,      width: width,    );