import 'package:flutter/material.dart';Widget AppTextField ({  required String labal,  required String hintText,  required IconData icon ,  required TextEditingController controller ,required BuildContext context ,}) {  return   TextFormField(    decoration: InputDecoration(      hintText: hintText,      hintStyle: TextStyle(        fontSize: 13,      ),      floatingLabelBehavior: FloatingLabelBehavior.always,      contentPadding: EdgeInsets.symmetric(vertical: 5,horizontal: 30),      border: OutlineInputBorder(borderRadius: BorderRadius.circular(20),),      label: Container(      margin: EdgeInsets.symmetric(horizontal: 5,),          child: Text(labal),      ),      labelStyle: Theme.of(context).textTheme.bodyText1,      suffixIcon: Icon(icon),    ),  );}Widget spaca (double height,double width)=>SizedBox(  height: height ,  width: width,);